{
  "version": "0.2",
  "phases": {
    "pre_build": {
      "commands": [
        "yarn install && yarn global add aws-cdk@1.73.0"
      ]
    },
    "build": {
      "commands": [
        "yarn run cdksynth"
      ]
    }
  },
  "artifacts": {
    "base-directory": "cdk.out",
    "files": "**/*"
  }
}

{
  "version": "0.2",
  "phases": {
    "build": {
      "commands": [
        "set -eu",
        "export InstanceId=\"$(node -pe 'require(process.env.CODEBUILD_SRC_DIR_Artifact_alfrescoocrpipelineCustomStagedevalfrescoocrdevAB156DDC_Outputs + \"/outputs.json\")[\"InstanceId\"]')\"",
        "export InstancePublicDnsName=\"$(node -pe 'require(process.env.CODEBUILD_SRC_DIR_Artifact_alfrescoocrpipelineCustomStagedevalfrescoocrdevAB156DDC_Outputs + \"/outputs.json\")[\"InstancePublicDnsName\"]')\"",
        "export LoadBalancerDnsName=\"$(node -pe 'require(process.env.CODEBUILD_SRC_DIR_Artifact_alfrescoocrpipelineCustomStagedevalfrescoocrdevAB156DDC_Outputs + \"/outputs.json\")[\"LoadBalancerDnsName\"]')\"",
        "sleep 240",
        "curl -Ssf $InstancePublicDnsName; RESULT=$? || aws ec2 get-console-output --instance-id $InstanceId --region eu-central-1 --output text\n    aws cloudformation delete-stack --stack-name alf-ocr-dev --region stageAccount.account.region}\n    exit $RESULT"
      ]
    }
  }
}

{
  "version": "0.2",
  "phases": {
    "build": {
      "commands": [
        "set -eu",
        "export InstanceId=\"$(node -pe 'require(process.env.CODEBUILD_SRC_DIR_Artifact_alfrescoocrpipelineCustomStageprodalfrescoocrprodFDDBCBFA_Outputs + \"/outputs.json\")[\"InstanceId\"]')\"",
        "export InstancePublicDnsName=\"$(node -pe 'require(process.env.CODEBUILD_SRC_DIR_Artifact_alfrescoocrpipelineCustomStageprodalfrescoocrprodFDDBCBFA_Outputs + \"/outputs.json\")[\"InstancePublicDnsName\"]')\"",
        "export LoadBalancerDnsName=\"$(node -pe 'require(process.env.CODEBUILD_SRC_DIR_Artifact_alfrescoocrpipelineCustomStageprodalfrescoocrprodFDDBCBFA_Outputs + \"/outputs.json\")[\"LoadBalancerDnsName\"]')\"",
        "sleep 240",
        "curl -Ssf $InstancePublicDnsName; RESULT=$? || aws ec2 get-console-output --instance-id $InstanceId --region us-east-1 --output text\n    \n    exit $RESULT"
      ]
    }
  }
}
